
public class Declaradores_operadores {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double a=5.2;
		 double b=7;
		
		 double c= b+a;
		 c++;
		
		 System.out.println(c);
		
		 c+=8;
		 System.out.println(c);
		 c= b/a;
		 System.out.println(c);
		 c= b*a-b;
		 System.out.println(c);
	}

}
